import React, { Component } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Grid,
  Select,
  TextArea,
  Label,
  Icon,
  Dimmer,
  Loader ,
  Divider
} from 'semantic-ui-react'


import fire from '../firebase';

import Urban from './Urban';
import Rural from './Rural';


class ProfileInfo extends Component {

    constructor(props){
        super(props);

        this.state= {toggle: true}

        this.toggleChange = this.toggleChange.bind(this);
      }


    toggleChange(){

      this.setState({toggle:!this.state.toggle})
  }

  render() {

    return ( <div>
      <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
<Grid.Column id="headerContainer"   style={{  backgroundColor: "#123445" }}>
  <Grid style={{ marginTop: "5vh"}}>
<Grid.Column  width={8}>
<Button color="yellow" size="massive" floated="right" onClick={this.toggleChange}>Urabn Customer</Button>
</Grid.Column>
<Grid.Column width={8}>
<Button color="yellow" size="massive" floated="left"  onClick={this.toggleChange}>Local Villager</Button>
</Grid.Column>

<Divider/>


</Grid>

{this.state.toggle ? <Urban/> : <Rural/>}


</Grid.Column>
</Grid>

  </div>
    )
  }
}

export default ProfileInfo;
