import React, { Component } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Grid,
  Select,
  TextArea,
  Label,
  Icon,
  Dimmer,
  Loader ,
  Divider
} from 'semantic-ui-react'


import fire from '../firebase';

import Urban from './Urban';
import Rural from './Rural';


class ProfileInfo extends Component {

    constructor(props){
        super(props);

        this.state= {toggle: true}

        this.VillagerDisable = this.VillagerDisable.bind(this);
        this.CustomerDisable = this.CustomerDisable.bind(this);

      }


    VillagerDisable = (e)=>{

      e.preventDefault();

      let btn= document.getElementById('cus');
      btn.classList.add("disabled");
  }

  CustomerDisable = (e)=>{

    e.preventDefault();

    let btn= document.getElementById('ret');
    btn.classList.add("disabled");
}



  render() {

    return ( <div>
      <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
<Grid.Column id="headerContainer"   style={{  backgroundColor: "#123445" }}>

  <h2 style={{ marginTop: "5vh" ,textAlign:"center" , color:"white"}}>Select Your Type...</h2>
  <Grid>
<Grid.Column  width={8}>
<Button id="cus" color="yellow" size="massive" floated="right" onClick={this.CustomerDisable}>Customer</Button>
</Grid.Column>
<Grid.Column width={8}>
<Button id="ret" color="yellow" size="massive" floated="left" onClick={this.VillagerDisable}  >Villager</Button>
</Grid.Column>

<Divider/>


</Grid>

{this.state.toggle ? <Urban type="Customer"/> : <Rural type="Villager"/>}


</Grid.Column>
</Grid>

  </div>
    )
  }
}

export default ProfileInfo;
