import React, { Component } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Grid,
  Select,
  TextArea,
  Label,
  Icon,
  Dimmer,
  Loader
} from 'semantic-ui-react'



import fire from '../firebase';

const options = [
    { key: 'm',value: 'male', text: 'Male'  },
    { key: 'f', value: 'female' , text: 'Female' },
    { key: 'o', value: 'other' , text: 'Other' },
  ]

const townOptions =[{value:"Mumbai" , text:"Mumbai"} , {value:"Chennai" , text:"Chennai"} , 
{value:"Delhi" , text:"Delhi"}]

const villOptions =[ 
  { town:"Mumbai" , 
 village:[{value:"Mumvill1" , text:"Mumvill1" }, {value:"Mumvill2" , text:"Mumvill2"} ]
} ,

{ town : "Delhi" ,
village:[{value:"Delvill1" , text:"Delvill1" }, {value:"Delvill2" , text:"Delvill2"} ] 

} ,

{
  town:"Chennai" ,
  village:[{value:"Chenvill1" , text:"Chenvill1" }, {value:"Chenvill2" , text:"Chenvill2"}]
},
{
  town:"xxx" ,
   village:[]
}

]






class Rural extends Component {

    constructor(props){
        super(props);

        this.state= {
          type : "",
             uname:"" ,
             gender:  "" ,
             contact: 12 ,
             town:"xxx" ,
             village:"" ,
             value: "",
             address :"",
             image:"",
             villOptions:[] ,
             email:"",
             authUid : "",
             profileComplete :false
        }

        this.handleChange = this.handleChange.bind(this);
        this.submit = this.submit.bind(this);
        this.getvillOptions = this.getvillOptions.bind(this);
    }

    submit =  (e) =>{

    }

    getvillOptions = () =>{
      let t1 =this.state.town;

      return villOptions[t1];

    }

    handleChange = e => {
        e.preventDefault();
      this.setState( { [e.target.name] : e.target.value } );
      console.log(this.state);
    };

    handleDropDownChange1 =(e ,{ name , value})=>{
       e.preventDefault();
       this.setState({ [name] : value , villOptions : villOptions[value] });

    }
    handleDropDownChange2 =(e ,{ name , value})=>{
      e.preventDefault();
      this.setState({ village : value })

   }





    render(){
      return(<div>
        <div style={{ backgroundColor: "lightcoral", borderRadius:"2%" ,
marginRight: "10vh", marginLeft: "10vh",padding:"5px", marginTop:"5vh" , height:'auto' }}>
  <h1 style={{color:"#123445" , textAlign:"center"}} > ADD Profile Info : <b > {this.props.type}</b> </h1>




 <Form>
   <Form.Group widths='equal'>
   <Form.Input fluid label='User name' name = "uname" type="text"
       onChange={this.handleChange} required /> 

       <Form.Dropdown
       fluid
       selection
       label='Gender'
       options={options}
       placeholder='Gender'
       name='gender'
       value={this.state.gender}
       onChange={this.handleDropDownChange}
     />
   </Form.Group>



   <Form.Group  widths='equal' style={{width:"35%"}}>

         <Form.Dropdown
              fluid
              selection
              search={true}
              name="town"
              label="Town"
              options={townOptions}
              value={this.state.town}
              placeholder='Select Town'
              onChange={this.handleDropDown1Change1}
            />
            </Form.Group> 

            <Form.Group  widths='equal' style={{width:"35%"}}>

         <Form.Dropdown
              fluid
              selection
              search={true}
              name="village"
              label="Village"
              options={this.state.villOptions}
              value={this.state.village}
              placeholder='Select Village'
              onChange={this.handleDropDown2Change2}
            />
            </Form.Group>




   <Form.Group widths='equal'  style={{width:"33%"}} >

    <Form.Input label='Contact No.'  placeholder='XXX'  type="number" name="contact" onChange={this.handleChange}/>
   </Form.Group>

   <Form.Input label='Picture Src' type='text'  name="image" onChange={this.handleChange}/>

   <Form.TextArea label=' Full Adress' name="address" placeholder=' Full Address'onChange={this.handleChange} />

   <Form.Button type="submit" onClick={this.submit} >Submit</Form.Button>

 </Form>

 <br/>



 </div>
   </div>)
    }


}

export default Rural;
