import {
    Button,
    Form,
    Header,
    Image,
    Icon,
    Loader,
    Divider,
    Label,
    Table,
    Transition
  } from "semantic-ui-react";
  import React, { Component } from "react";
  import fire from "../firebase";
  import "../App.css";

  class Account extends Component {
    constructor(props) {
      super(props);
      this.state = {

        loading: true,
        loggedIn: true,
        showSetting: false,
        showEdit: false,

      };
    }

    // Auth Change Listener
    componentWillMount = () => {
      fire.auth().onAuthStateChanged(user => {
        if (user) {
          this.setState({ loggedIn: true, showSetting: true , loading:false });

        } else {
          this.setState({ loading: false, loggedIn: false });
        }
      });
    };

    // Form Handler
    handleChange = e => {
      this.setState({ [e.target.name]: e.target.value });
    };





    render() {

    return (
        <div style={{
            backgroundColor: "#f9f9f9",
            marginTop: "0vh",
            minHeight: "100vh"
          }}
        >
          <div
            id="headerContainer"
            style={{ marginTop: "0vh", minHeight: "100vh" }}
          >

            {this.state.loggedIn ? (
              <div>
                <Header as="h2" style={{ paddingTop: "3vh", marginTop: "0vh" }}>
                  <Icon name="setting" />
                  <Header.Content>Manage Account</Header.Content>
                </Header>
                <Divider />


                {this.state.showSetting ? (
                  <Button
                    color={this.state.showEdit ? "white" : "green"}
                    size="small"
                    onClick={() => this.edit()}
                  >
                    <Button.Content visible>
                      {this.state.showEdit
                        ? "DISABLE EDIT MODE"
                        : "ENABLE EDIT MODE"}
                    </Button.Content>
                  </Button>
                ) : null}
              </div>
            ) : null}

            {/* CONTAINER */}
            {this.state.showEdit ?  
            <div>
            <Transition
           visible={this.state.showEdit}
            animation="fade"
            duration={400}
          >
            <Form
              style={{
                paddingBottom: "8vh",
                paddingTop: "2vh"
              }}
            >
              <Form.Group widths="equal">
                <Form.Input
                  onChange={this.handleChange}
                  name="updateTitle"

                  required
                />
                <Form.Input
                  fluid
                  onChange={this.handleChange}
                  name="updatePicture"

                  required
                />
              </Form.Group>
              <Form.TextArea
                rows={4}
                onChange={this.handleChange}
                name="updateDescription"

                required
              />

              <Button
                as="div"
                compact
                labelPosition="left"
                size="small"

              >
                <Label as="span" color="black">
                  UPDATE
                </Label>
                <Button color="yellow" icon compact>
                  <Icon name="pencil" color="black" />
                </Button>
              </Button>
            </Form>
          </Transition>
        </div> : null }


            {/* SPINNER */}
            {this.state.loading ? (
              <Loader style={{ marginTop: "5%" }} active inline="centered" />
            ) : null}
          </div>
        </div>
      );
    }
  }

  export default Account;
