import fire from '../firebase';
import {
    Button,
  Container,
  Card,
  Icon,
  Image,
  Grid,
  Loader,
  Divider,
  Label,
  Form
  } from "semantic-ui-react";

  import { Route,
    Link,
    BrowserRouter as Router , 
    Switch} from 'react-router-dom';

    import moment from 'moment';


  class FoodItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
        loading: true,
        list: [],
        keys: []

      };


    }
    componentDidMount = () => 
    {

     fire.database()
        .ref(`fooditem/`)
        .on("value", snapshot => {
          var obj = snapshot.val();
          var list = [];
          var keys = [];
          for (let a in obj) {
            list.push(obj[a]);
            keys.push(a);
          }
          this.setState({
            list: list,
            keys: keys,
            loading: false
          } , console.log(this.state.list));


        }); 


    }


    render()
    { return(<div  >
        {this.state.loading ? (
              <div>
                <Loader style={{ marginTop: "25%" }} active inline="centered" />
              </div>
            ) : null}
            <div id="headerContainer" style={{width:"75%"}}>
            <Card.Group itemsPerRow={3} textAlign='center'  >
              { this.state.list.map((item, index) =>
           Object.values(item).map((nestedItem, nestedIndex) => (
            <Card   >
             {console.log("vh")}
              <Card.Content>
                <Label as="p" color="white" size="large" ribbon>
                  Score : nestedItem.score
                </Label>
                <Card.Header style={{ paddingTop: "2vh" }}>
                  nestedItem.title
                </Card.Header>
                <Card.Description>nestedItem.description</Card.Description>
              </Card.Content>
              <Card.Content extra>
                <p>
                  <Icon name="user" style={{ marginRight: "5%" }} />
                  by nestedItem.user
                </p>
                <Divider />
                <p>
                  <Icon name="clock" style={{ marginRight: "5%" }} />
                 hhh
                </p>

              </Card.Content>
            </Card>
             )) )
           }

     </Card.Group>
     </div>




    </div>)

    }

  }


    export default FoodItem ;
