import React, { Component } from "react";
import fire from "../firebase";

import { Route,
    Link,
    BrowserRouter as Router , 
    Switch} from 'react-router-dom'

import {
    Form,
    Grid,
    Loader,
    Divider,
    Header,
    Message,
    Button
  } from "semantic-ui-react";

  import Login from './Login';
  import Signup from './Signup';

  import Axios from 'axios';




  class Auth extends Component {
    constructor(props) {
      super(props);
      this.state = {
          toggle:true
      }
      this.toggleChange = this.toggleChange.bind(this);
    }


    componentDidMount(){

      let api = "https://api.data.gov.in/resource/cb579e8f-e4e0-48eb-a058-1fc812a38ff2?api-key=579b464db66ec23bdd000001cdd3946e44ce4aad7209ff7b23ac571b&format=json&offset=10&limit=10"

      Axios.get(api, {

        params: {
            records : [{district_name: "Saharanpur" , sub_district_name: "Behat",}]
        }

    }).then( (response) => {

        console.log(response);

    }).catch((response) => {
        console.error(response);
    });

    }

    toggleChange(){

        this.setState({toggle:!this.state.toggle})
    }
    render(){
        return(
            <div>
                <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
          <Grid.Column id="headerContainer"   style={{  backgroundColor: "#123445" }}>
            <Grid style={{ marginTop: "5vh"}}>
          <Grid.Column  width={8}>
     <Button color="yellow" size="massive" floated="right" onClick={this.toggleChange}> Login</Button>
    </Grid.Column>
    <Grid.Column width={8}>
    <Button color="yellow" size="massive" floated="left"  onClick={this.toggleChange}> Signup</Button>
    </Grid.Column>

    <Divider/>


    </Grid>

    {this.state.toggle ? <Login/> : <Signup/>}


          </Grid.Column>
          </Grid>

            </div>
        )



    }




}


export default Auth;
