import React, { Component } from "react";
import fire from '../firebase';
import {
    Button,
  Container,
  Card,
  Icon,
  Image,
  Grid,
  Loader,
  Divider,
  Label,
  Form,
  Table,
  Input
  } from "semantic-ui-react";

  import { Route,
    Link,
    BrowserRouter as Router , 
    Switch} from 'react-router-dom';

    import moment from 'moment';


  class FoodItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
        loading: true,
        list: [],
        keys: []

      };


    }
    componentDidMount = () => 
    {

         fire.database()
        .ref(`/foodItem/`)
        .on("value", snapshot => {
          var obj = snapshot.val();
          var list1 = [];
          var keys1 = [];
          for (let a in obj) {
            list1.push(obj[a]);
            keys1.push(a);
            console.log(obj[a]);
          }
          this.setState({
            list: list1,
            keys: keys1

          } , ()=> this.setState({loading: false}));


        }) ; 


    }


    render()
    { return(<div  >
        {this.state.loading ? (
              <div>
                <Loader style={{ marginTop: "25%" }} active inline="centered" />
              </div>
            ) : null}


            <h1>Shoping Cart</h1>


            <div >
             <Grid>
               <Grid.Column width={8}>




      <Container>
      { this.state.list.map((item, index) =>
        Object.values(item).map((nestedItem, nestedIndex) => (
          <div>

       <h2>{nestedItem.product}</h2>
        <h2>{nestedItem.cost} per {nestedItem.metric} </h2>
        <h2>{nestedItem.quantity}</h2>
        <Input/>
        <Button>ADD TO Cart</Button>
    </div>
    )) ) }



    </Container>  







               </Grid.Column>
               <Grid.Column width={6}>





               </Grid.Column>
               </Grid> 


    </div>
    </div>

    )}

  }


    export default FoodItem ;
