import React ,{Component} from 'react'
import moment from "moment";
import fire from '../firebase'

import {
  Button,
  Container,
  Card,
  Header,
  Image,
  Grid,
  Loader,
  Icon,
  Dimmer,
  Divider,
  Form ,
  Comment,
  GridColumn
} from "semantic-ui-react";






var qs = require('querystringify');




class Welcome extends Component {

    constructor(props) {
        super(props);
        this.state = { key1:"" , key2:"" ,  loading :false,

        product :"" ,
        cost : " " ,
        quantity:"" ,
        retailer:"" ,
        showSetting :  false ,

        showPostItem: false,
        list: [],
        keys:[],

        cost : 0 ,
        modal: 0



    };
     this.Details = this.Details.bind(this);
     this.back = this.back.bind(this).bind(this);
     this.getRetailerDetail = this.getRetailerDetail.bind(this);

      }

      Details =  (nestedId, id) => {
          fire
          .database()
          .ref(
            `/foodItem/${id}/${nestedId}`
          )
          .once("value", snapshot => {
            var obj = snapshot.val();

      this.setState({product : obj.product ,
              cost : obj.product ,
              quantity :  obj.quantity

            })

            this.getRetailerDetail(obj.retailer);


          });

      };


    back = () =>
    {
        this.props.history.push("/");
    }

    getRetailerDetail(retailer){
       console.log(retailer);

    }


   componentWillMount (){


    var {id} = qs.parse(this.props.match.params.id); 
    var {id2} = qs.parse(this.props.match.params.id2); 

    this.Details(id2 , id);

    this.setState({ key1: id , key2: id2  ,loading :false});


   }



    render() {

      const listItems = this.state.list.map((item, index) => (
        <div>
          <Table fixed stackable
            style={{ marginTop: "4vh", marginBottom: "0vh" }}
          >
            <Table.Body>
             <Table.Row style={{background:"lightcoral" , color:"black"  }}>
              <Table.Cell width={4} >
               <h2>{item.product}</h2>
              </Table.Cell>

              <Table.Cell width={3}>
                <h2>Quantity : {item.quantity}</h2>
              </Table.Cell>
              <Table.Cell width={4}>
                <h3> Cost per {item.metric} :Rs. {item.cost}</h3>
              </Table.Cell>


              <Table.Cell textAlign="center" width={3}>




                {this.state.showSetting ?
                   <Button size="small"color={this.state.showPostItem ? "white" : "yellow"}
                    onClick={() => this.buy(item ,index)}>
                  <Button.Content visible>
                  <Icon name='pencil' />
                      {this.state.showPostItem
                        ? "DISABL BUY EDIT"
                        : "ENABLE BUY MODE"}
                    </Button.Content>
                </Button> : null }

              </Table.Cell>
              </Table.Row>
            </Table.Body>
          </Table>

          <Transition
  visible={this.state.showPostItem && this.state.modal === index}
  animation="vertical flip"
  duration={400}
  >
  <Form
    style={{
      paddingBottom: "8vh",
      paddingTop: "2vh"
    }}
  >
    <Form.Group widths="equal">

      <Form.Input
        fluid
        onChange={this.handleChange}
        name="quantity"
        label ="Quantity"
        required
      />
    </Form.Group>

    <Button as="div" compact
      labelPosition="left"
      size="large"
      onClick={() => this.update(index)}
    >
      <Label as="span" color="black">
        UPDATE
      </Label>
      <Button color="yellow" icon compact>
        <Icon name="pencil" color="black" />
      </Button>
    </Button>
  </Form>
  </Transition>


        </div>
      ));




       return <div>

       {this.state.loading ? (
        <Loader style={{ marginTop: "5%" }} active inline="centered" />
      ) : null}

       <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
         <Grid.Column id="headerContainer"   style={{ backgroundColor: "#123445" }}>
          <Button
                  color="green"
                  size="compact" fluid
                  onClick={() => this.showSetting()}
                >
                  <Button.Content visible>
                    {this.state.showSetting
                      ? "DISABLE BUY ITEMS MODE": "BUY ITEMS MODE"
                      }
                  </Button.Content>
                </Button>
          {this.state.showSetting ? <div>{listItems}</div> : null}

      <Button positive onClick={this.back} > Back</Button>
      </Grid.Column>
      </Grid>
      </div>

    }
  }

  export default Welcome;
