import React, { Component } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Grid,
  Select,
  TextArea,
  Label,
  Icon,
  Dimmer,
  Loader
} from 'semantic-ui-react'



import fire from '../firebase';

const options = [
    { key: 'm',value: 'male', text: 'Male'  },
    { key: 'f', value: 'female' , text: 'Female' },
    { key: 'o', value: 'other' , text: 'Other' },
  ]



class Rural extends Component {

    constructor(props){
        super(props);

        this.state= {}

        this.handleChange = this.handleChange.bind(this);
        this.submit = this.submit.bind(this);
    }

    submit =  (e) =>{

    }

    handleChange = e => {
        e.preventDefault();
      //this.setState( { [e.target.name] : e.target.value } );
      console.log(this.state);
    };




    render(){
      return(<div>
        <div style={{ backgroundColor: "lightcoral", borderRadius:"2%" ,
marginRight: "10vh", marginLeft: "10vh",padding:"5px", marginTop:"5vh" , height:'auto' }}>
  <h1 style={{color:"#123445" , textAlign:"center"}} > ADD Profile Info <b > {this.props.type}</b> </h1>




 <Form>
   <Form.Group widths='equal'>
   <Form.Input fluid label='User name' name = "uname" type="text"
       onChange={this.handleChange} required /> 

       <Form.Select
       fluid
       label='Gender'
       options={options}
       placeholder='Gender'
       name='sex'
       onChange={this.handleSelectChange}
     />
   </Form.Group>


   <Form.Group widths='equal'  >
   <Form.Select
       fluid
       label='Town'
       options={options}
       placeholder='Town Name'
       name='town'
       onChange={this.handleSelectChange}
     /> 
     <Form.Select
       fluid
       label='Village'
       options={options}
       placeholder='Village Name'
       name='vill'
       onChange={this.handleSelectChange}
     /> 
   </Form.Group>



   <Form.Group widths='equal'  style={{width:"33%"}} >

    <Form.Input label='Contact No.'  placeholder='XXX'  type="number" name="contact" onChange={this.handleChange}/>
   </Form.Group>

   <Form.Input label='Picture Src' type='text'  name="image" onChange={this.handleChange}/>

   <Form.TextArea label=' Full Adress' name="about" placeholder=' Full Address'onChange={this.handleChange} />

   <Form.Button type="submit" onClick={this.submit} >Submit</Form.Button>

 </Form>

 <br/>



 </div>
   </div>)
    }


}

export default Rural;
