import _ from 'lodash'
import React, { Component } from 'react'
import { Button, Dropdown, Grid, Header } from 'semantic-ui-react'

const getOptions =  [
    { key: 'm',value: 'male', text: 'Male'  },
    { key: 'f', value: 'female' , text: 'Female' },
    { key: 'o', value: 'other' , text: 'Other' },
  ]


class DropdownExampleRemote extends Component {
  state = {
    id1:{ search: true,
        searchQuery: null,
        value: "",
        options: getOptions
  },
  id2:{ search: true,
    searchQuery: null,
    value: "",
    options: getOptions}

}

  handleChange = (e, { value , name }) => this.setState({[name]:{ value} })

  handleSearchChange = (e, { searchQuery }) => this.setState({ searchQuery })





  render() {
    const { multiple, options, isFetching, search, value } = this.state

    return (
      <Grid>
        <Grid.Column width={6}>
          <Dropdown
            fluid
            selection
            name="id1"
            search={this.state.id1.search}
            options={this.state.id1.options}
            value={this.state.id1.value}
            placeholder='Add Users'
            onChange={this.handleChange}
            onSearchChange={this.handleSearchChange}

          /></Grid.Column>
          <Grid.Column width={6}>
          <Dropdown
            fluid
            selection
            search={this.state.id2.search}
            options={this.state.id2.options}
            value={this.state.id2.value}
            placeholder='Add Users'
            onChange={this.handleChange}
            onSearchChange={this.handleSearchChange}

          />

        </Grid.Column>


        <Grid.Column width={6}>
          <Header>State</Header>
          <pre>{JSON.stringify(this.state, null, 2)}</pre>
        </Grid.Column>
      </Grid>
    )
  }
}

export default DropdownExampleRemote
