import React ,{Component} from 'react'
import moment from "moment";
import fire from '../firebase'

import {
  Button,
  Container,
  Card,
  Header,
  Image,
  Grid,
  Loader,
  Icon,
  Dimmer,
  Divider,
  Form ,
  Comment,
  GridColumn
} from "semantic-ui-react";

import "../User/profile.css"

var qs = require('querystringify');




class BuynowVillage extends Component {

    constructor(props) {
        super(props);
        this.state = { key1:"" , key2:"" ,  loading :true,

          description:"" ,
            product:"" ,
            quantity : 0 ,
            cost : "" ,
            mertric : "" ,
            user: "" ,
            retailer  :"" ,

        } 




     this.Details = this.Details.bind(this);
     this.PersonDetails = this.PersonDetails.bind(this);

      }

      Details =  (nestedId, id) => {
          fire
          .database()
          .ref(
            `/foodItem/${id}/${nestedId}`
          )
          .once("value", snapshot => {
            var obj = snapshot.val();
            var y = moment(obj.timestamp ,"LLL" ).format('YYYY-MM-DD');
            var x  =   moment(y).fromNow();

            this.setState(  {...this.state , loading:false, 
              product : obj.product , cost:obj.cost , quantity : obj.quantity ,
              metric : obj.metric 
            })

            this.PersonDetails(obj.retailer);

          });

      };

      PersonDetails(retailer){

        fire.database()
        .ref(`/people/${retailer}`)
        .once("value", snapshot => {
          var obj = snapshot.val();

          console.log(obj);
      });

      this.setState({loading : false});


    }


   componentWillMount (){


    var {id} = qs.parse(this.props.match.params.id); 
    var {id2} = qs.parse(this.props.match.params.id2); 


   this.Details(id2 , id);



    this.setState({ key1: id , key2: id2 });


   }



   render() {

    console.log(this.state);
    return (<div>


         {this.state.loading?<Loader active/>: null} 

      <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
          <Grid.Column id="headerContainer"   style={{ backgroundColor: "#123445" }}>

          <div id="main1">  
          <div className="user-container">
          <h1 className="title">this.state.product</h1>


          <div className="user-profile">
          <div class="avatar-container">
      <img src="https://thenypost.files.wordpress.com/2019/09/junk-food-turns-kid-blind.jpg?quality=90&strip=all&w=618&h=410&crop=1" alt="Photo" className="avatar" />
    </div>

            <h2 className="user-name">this.state.cost</h2>
            <h2 className="user-email">this.state.quantity</h2>

          </div>
  </div>
  </div>

          </Grid.Column>



     </Grid>
        </div>
        )
}
  }  

  export default BuynowVillage;
