import React, { Component } from "react";
import fire from "../firebase";
import {

  Grid, Loader,

  } from "semantic-ui-react";

  import  './profile.css';








  class UserProfile extends Component {
    constructor(props) {
      super(props);
      this.state = {loading : true , list:[]};

    }

    componentWillMount()
    {
         fire.auth().onAuthStateChanged(user => {
            if (user) {
              checking(user.email);
            }
            else{
              console.log("{}{}{}")
            }
          });
    }

    checking(email){

        this.setState({loading:true});



  fire.database()
  .ref(`/people/`)
  .once("value", snapshot => {
    var obj = snapshot.val();


  for (let a in obj) {
    if(obj[a].email === email )
    {
      this.setState({list:obj[a]});
    }


    };

    this.setState({loading:false});
  });  


 }







render() {
    return (<div>


         {this.state.loading?<Loader active/>: null} 

      <Grid  style={{ marginTop: "0vh", minHeight: "100vh"  }}>
          <Grid.Column id="headerContainer"   style={{ backgroundColor: "#123445" }}>

          <User list={this.state.list}/>

          </Grid.Column>



     </Grid>
        </div>
        )
}
  }

  const User = (props) =>
{
    return <div id="main1">  
          <div className="user-container">
          <h1 className="title">{props.name}</h1>


          <div className="user-profile">
          <div class="avatar-container">
      <img src="https://images.pexels.com/photos/756453/pexels-photo-756453.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" alt="Photo" className="avatar" />
    </div>

            <h2 className="user-name">this.state.userName</h2>
            <h5 className="user-email">this.state.userEmail</h5>

          </div>
  </div>
  </div>

  }


export default UserProfile;
